# Hier staan de gegevens + formules voor de "oude" organismen met ligase 1
mRNA_Ce = ""
mRNA_Xl = ""
mRNA_Hs = ""
mRNA_Dd = ""
mRNA_At = ""
c = "C"
g = "G"
GC_mRNA_Dd = 100/len(mRNA_Dd)*(mRNA_Dd.count(c)+mRNA_Dd.count(g)) # datab. GC% = 22.46
GC_mRNA_Hs = 100/len(mRNA_Hs)*(mRNA_Hs.count(c)+mRNA_Hs.count(g)) # datab. GC% = 40.9
GC_mRNA_Xl = 100/len(mRNA_Xl)*(mRNA_Xl.count(c)+mRNA_Xl.count(g)) # datab. GC% = 40.2325
GC_mRNA_At = 100/len(mRNA_At)*(mRNA_At.count(c)+mRNA_At.count(g)) # datab. GC% = 36.9
GC_mRNA_Ce = 100/len(mRNA_Ce)*(mRNA_Ce.count(c)+mRNA_Ce.count(g)) # datab. GC% = 35.4342
# Hier begint het script voor de interactiviteit (als er #hier bijstaat = nog onder constructie)
print ("Hai. I calculate the GC% of mRNA form an organism."
       " I have five different organisms ready for you.")
q1 = input("Do you want to know the GC% of one of these organisms?"
           " Or do you want me to calculate a new one? (old/new): ")
if q1 == "new":
    name_organism = input.__call__("What is the name of the organism? ")
    mRNA_organism = input.__call__("Put the mRNA sequence here (without enters!): ")
    GC_mRNA_organism = 100/len(mRNA_organism)*(mRNA_organism.count(c)+mRNA_organism.count(g))
    print ("The GC% for", name_organism, "is", GC_mRNA_organism)
    quit
elif q1 == "old":
    print("Alright. Choose an organism:" #Zoek uit hoe je hier een enter kan toevoegen
          "- Caenorhabditis elegans (Ce)"
          "- Dictyostelium discoideum (Dd)"
          "- Homo sapiens (Hs)"
          "- Xenopus laevis (Xl)"
          "- Arabidopsis thalia (At)")
    old = input("(Don't forget the capslock on the first letter!): ") #Zin aanpassen
    if old == "Ce":
        print ("The GC% for Caenorhabditis elegans is 35.4342")
        print ("The GC% on ligase 1 is", GC_mRNA_Ce)
        quit
    elif old == "Dd":
        print ("The GC% for Dictyostelium discoideum is 22.46")
        print ("The GC% on ligase 1 is", GC_mRNA_Dd)
        quit
    elif old == "Hs":
       print ("The GC% for Homo sapiens is 40.9")
       print ("The GC% on ligase 1 is", GC_mRNA_Hs)
       quit
    elif old == "Xl":
        print ("The GC% for Xenopus laevis is 40.2325")
        print ("The GC% on ligase 1 is", GC_mRNA_Xl)
        quit
    elif old == "At":
        print ("The GC% for Arabidopsis thalia is 36.9")
        print ("The GC% on ligase 1 is", GC_mRNA_At)
        quit
    else:
        print ("Sorry, I do not understand. Quitting.")
        quit
else:
    print("Sorry, I do not understand. Quitting.")
    quit
